package main

import "fmt"

func nextNCharsDiffer(s string, n int) bool {
	bytes := map[byte]bool{}
	for j := 0; j < n; j++ {
		bytes[s[j]] = true
	}
	return len(bytes) == n
}

func partOne(s string) int {
	for i := 0; i < len(s)-4; i++ {
		if nextNCharsDiffer(s[i:], 4) {
			return i + 4
		}
	}
	return -1
}

func partTwo(s string) int {
	for i := 0; i < len(s)-14; i++ {
		if nextNCharsDiffer(s[i:], 14) {
			return i + 14
		}
	}
	return -1
}

func main() {
	content := ""

	fmt.Printf("Part 1: %v\n", partOne(content))
	fmt.Printf("Part 2: %v\n", partTwo(content))
}
